import java.io.IOException;
import java.text.SimpleDateFormat;
import java.util.ArrayList;
import java.util.Date;
import java.util.List;
@SuppressLint("ValidFragment")
public class FraMyFragment extends Fragment {
    private DBCOMUTIL dbcomutil;
    private View rootView;
    private RecyclerView recyclerView;
    private List<Data> itemsData;
    private int layout;
    public FraMyFragment(int layout) {
        this.layout = layout;
    }
    public FraMyFragment initData(List<Data> itemsData) {
        this.itemsData = itemsData;
        return this;
    }
    public void updateData(List<Data> itemsData) {
        this.itemsData = itemsData;
        setAdapter();
    }
    private void setAdapter() {
        recyclerView.setLayoutManager(new LinearLayoutManager(getActivity()));
        RecyclerMyListAdapter mAdapter = new RecyclerMyListAdapter(itemsData, getContext(), layout);
        recyclerView.setAdapter(mAdapter);
        recyclerView.setItemAnimator(new DefaultItemAnimator());
        DividerItemDecoration dividerItemDecoration = new DividerItemDecoration(getContext(), DividerItemDecoration.VERTICAL);
        Drawable drawable = ContextCompat.getDrawable(getContext(), R.color.LineColor);
        dividerItemDecoration.setDrawable(drawable);
        recyclerView.addItemDecoration(dividerItemDecoration);
        mAdapter.setOnItemClickListener(new RecyclerMyListAdapter.OnItemClickListener() {
            @Override
            public void onItemClick(int position, int layout) {
                if (layout == R.layout.list_choose_main) {
                    Intent intent = new Intent();
                    intent.putExtra("type", itemsData.get(position).type);
                    intent.putExtra("title", itemsData.get(position).title);
                    intent.setClass(getContext(), ListPoemActivity.class);
                    startActivity(intent);
                } else if (layout == R.layout.list_item02) {
                    if (!itemsData.get(position).poem_title.equals("title")) {
                        Intent intent = new Intent();
                        intent.putExtra("positon", position);
                        Gson json = new Gson();
                        intent.putExtra("data", json.toJson(itemsData));
                        intent.setClass(getContext(), PoemActivity.class);
                        startActivity(intent);
                    }
                } else if (layout == R.layout.list_item01) {
                } else if (layout == R.layout.list_item03) {
                }
            }
        });
    }
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        if (layout == R.layout.list_item03) {
            rootView = inflater.inflate(R.layout.activity_content, container, false);
            recyclerView = rootView.findViewById(R.id.list);
            setAdapter();
            initView();
        } else if (layout == R.layout.list_item05) {
            rootView = inflater.inflate(R.layout.list_item05, container, false);
            ImageView imageView = rootView.findViewById(R.id.one);
            imageView.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    Intent intent = new Intent(getContext(), AnalogyExaminationActivity.class);
                    getContext().startActivity(intent);
                }
            });
        } else if (layout == R.layout.list_item04) {
            rootView = inflater.inflate(R.layout.list_item04, container, false);
            new Thread(new Runnable() {
                TextView tuijan = rootView.findViewById(R.id.tuijan);
                TextView title1 = rootView.findViewById(R.id.title);
                TextView author1 = rootView.findViewById(R.id.author);
                TextView content1 = rootView.findViewById(R.id.content);
                ImageView imageView = rootView.findViewById(R.id.tuijiantupian);
                Data data = new Data();
                String[] urls = new String[]{
                        "https://images.unsplash.com/photo-1467151568115-d968fdf5d292?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60",
                        "https://images.unsplash.com/photo-1553843874-90537c8d63d1?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&w=1000&q=80",
                        "https://images.unsplash.com/photo-1522628431849-2d72fce61a67?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60",
                        "https://images.unsplash.com/photo-1522921575-9d14e748e6da?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60",
                        "https://images.unsplash.com/photo-1489537235181-fc05daed5805?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60",
                        "https://images.unsplash.com/photo-1467151568115-d968fdf5d292?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"
                };
                @Override
                public void run() {
                    try {
                        String sentence = "https://v2.jinrishici.com/sentence";
                        String doc = Jsoup.connect(sentence).timeout(1000000)
                                .ignoreContentType(true)
                                .header("X-User-Token", "qEuk3pTNRgzX/pSnuKO7N2S1ovP3NsGq")
                                .execute().body();
                        JsonObject returnData = new JsonParser().parse(doc).getAsJsonObject();
                        JsonObject jsonobj1 = returnData.get("data").getAsJsonObject();
                        JsonObject jsonobj2 = jsonobj1.get("origin").getAsJsonObject();
                        JsonElement content = jsonobj2.get("content");
                        data.poem_shortcut = jsonobj1.get("content").getAsString();
                        data.poem_title = jsonobj2.get("title").getAsString();
                        String dynasty = jsonobj2.get("dynasty").getAsString();
                        data.poem_author = dynasty + "：" + jsonobj2.get("author").getAsString();
                        String[] cotent = new Gson().fromJson(content.toString(), String[].class);
                        for (int j = 0; j < cotent.length; j++) {
                            data.poem_content += cotent[j];
                        }
                    } catch (IOException e) {
                        // TODO Auto-generated catch block
                        e.printStackTrace();
                    }
                    getActivity().runOnUiThread(new Runnable() {
                        @Override
                        public void run() {
                            tuijan.setText(data.poem_shortcut);
                            title1.setText(data.poem_title);
                            author1.setText(data.poem_author);
                            content1.setText(data.poem_content);
                            Glide.with(getContext()).load(urls[(int) (Math.random() * urls.length)]).into(imageView);
                        }
                    });
                }
            }).start();
        } else {
            rootView = inflater.inflate(R.layout.recycler_content, container, false);
            recyclerView = rootView.findViewById(R.id.list);
            setAdapter();
        }
        return rootView;
    }
    private void initView() {
        TextView button = rootView.findViewById(R.id.detail_page_do_comment);
        button.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                showCommentDialog();
            }
        });
    }
    private void showCommentDialog() {
        View commentView = LayoutInflater.from(getContext()).inflate(R.layout.comment_dialog_layout, null);
        final EditText commentText = (EditText) commentView.findViewById(R.id.dialog_comment_et);
        final Button bt_comment = (Button) commentView.findViewById(R.id.dialog_comment_bt);
        dialog = new BottomSheetDialog(getContext(), R.style.BottomSheetEdit);
        dialog.setContentView(commentView);
        View parent = (View) commentView.getParent();
        BottomSheetBehavior behavior = BottomSheetBehavior.from(parent);
        commentView.measure(0, 0);
        behavior.setPeekHeight(commentView.getMeasuredHeight());
        bt_comment.setOnClickListener(new View.OnClickListener() {

            @Override
            public void onClick(View view) {
                SimpleDateFormat formatter = new SimpleDateFormat("yyyy年MM月dd日HH:mm:ss");
                Date curDate = new Date(System.currentTimeMillis());
                String time = formatter.format(curDate);
                dbcomutil = new DBCOMUTIL(getContext());
                dbcomutil.createDB();
                if (commentText.getText().toString().equals("")) {
                    Toast.makeText(getContext(), "请输入文字", Toast.LENGTH_SHORT).show();
                } else {
                    dbcomutil.add(commentText.getText().toString() + "Time:" + time);
                    TextView textView = rootView.findViewById(R.id.page_comments);
                    List<Data> yujunyu = new ArrayList<>();
                    List<String> li = dbcomutil.select();
                    String[] strs = {"风月", "三生石", "冰冰", "王者风范", "雨渐", "丹缺"};
                    String[] urls = {
                            "data:image/jpeg;base64,/9j/4AAQSkZJRgABAQAAAQABAAD/2wBDAAgGBgcGBQgHBwcJCQgKDBQNDAsLDBk" +
                                    "" +
                                    "RRnFI3FBqbmouetJuo7GigXQ3rSTfAmPSuY+JsIm8B3x7o0bD8HFdFpv/Ht+NYXxG48B6l/ur/6EKtHG9zS8J3raj4W066f70kC5+o4/pWziuU+HDE+A9MyeiuP/HzXWCrQz//Z",
                            "http://img1.imgtn.bdimg.com/it/u=1708037700,3618178295&fm=26&gp=0.jpg",
                            "data:image/jpeg;base64," +
                                    "KKACiiigAooooAKKKKACiiigAooooAKKKKACiiigAooooAKKKKACi" +
                                    "iigAooooAKKKKACiiigAooooAKKKKACiiigAooooAKKKKACiiigAooooAKKKKACiiigAooooA/9k=",
                            "http://img2.imgtn.bdimg.com/it/u=1510682223,987467705&fm=26&gp=0.jpg",
                            "http://img3.imgtn.bdimg.com/it/u=296836973,370922044&fm=26&gp=0.jpg",
                            "http://img5.imgtn.bdimg.com/it/u=132370356,1876440941&fm=26&gp=0.jpg",
                    };
                    for (int i = 0; i < li.size(); i++) {
                        Data data = new Data();
                        data.poem_content = li.get(i);
                        data.title = strs[i];
                        data.poem_url = urls[i];
                        yujunyu.add(data);
                    }
                    updateData(yujunyu);
                    dialog.dismiss();
                }
            }
        });
        commentText.addTextChangedListener(new TextWatcher() {
            @Override
            public void beforeTextChanged(CharSequence charSequence, int i, int i1, int i2) {
            }
            @Override
            public void onTextChanged(CharSequence charSequence, int i, int i1, int i2) {
                if (!TextUtils.isEmpty(charSequence) && charSequence.length() > 2) {
                    bt_comment.setBackgroundColor(Color.parseColor("#FFB568"));
                } else {
                    bt_comment.setBackgroundColor(Color.parseColor("#D8D8D8"));
                }
            }
            @Override
            public void afterTextChanged(Editable editable) {
            }
        });
        dialog.show();
    }
    private BottomSheetDialog dialog;
}
